mapper.select:
/* 구현 Sql */ SELECT a.CMMNTY_ID AS "cmmntyId" , a.CMMNTY_NM AS "cmmntyNm" , a.CMMNTY_ALIAS AS "cmmntyAlias" , a.USE_AT AS "useAt" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" , a.FRST_REGISTER_ID AS "frstRegisterId" , b.USER_NM AS "frstRegisterNm" FROM COMTN_CMMNTY a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.FRST_REGISTER_ID = b.USER_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM001') c ON a.REGIST_SE_CODE = c.CODE WHERE 1=1
AND a.CMMNTY_NM LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY a.FRST_REGIST_PNTTM DESC /* 구현 Sql */ LIMIT #{recordPerPage} OFFSET #{firstIndex}

SELECT CMMNTY_ID	
  FROM COMTN_CMMNTY
 WHERE CMMNTY_ALIAS = #{cmmntyAlis}

mapper.insert:
INSERT INTO COMTN_CMMNTY (
		CMMNTY_ID
	  ,	CMMNTY_NM
	  ,	CMMNTY_ALIAS
	  ,	CMMNTY_INTRCN
	  ,	REGIST_SE_CODE
	  ,	TMPLAT_ID
	  ,	USE_AT
	  , MEMBER_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
	  , CMMNTY_LOGO_IMAGE
	  , ADDITIONAL_INFO
) VALUES (
		#{cmmntyId}
	  , #{cmmntyNm}
	  , #{cmmntyAlias}
	  ,	#{cmmntyIntrcn}
	  ,	#{registSeCode}
	  ,	#{tmplatId}
	  ,	#{useAt}
	  , #{memberAt}
	  , #{frstRegisterId}
	  , sysdate()
	  , #{frstRegisterId} 
	  , sysdate()
	  , #{cmmntyLogoImage, jdbcType=BLOB}
	  , #{additionalInfo, jdbcType=VARCHAR}
)

mapper.update:
UPDATE COMTN_CMMNTY
   SET CMMNTY_NM = #{cmmntyNm}
     , CMMNTY_ALIAS = #{cmmntyAlias}
     , CMMNTY_INTRCN = #{cmmntyIntrcn}
     , TMPLAT_ID = #{tmplatId}
     , USE_AT = #{useAt}
     , MEMBER_AT = #{memberAt}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
     , ADDITIONAL_INFO = #{additionalInfo}

 	 , CMMNTY_LOGO_IMAGE = #{cmmntyLogoImage, jdbcType=BLOB}

 WHERE CMMNTY_ID = #{cmmntyId}

mapper.select:
SELECT MNGR_AT
     , USE_AT 
  FROM COMTN_CMMNTY_USER
 WHERE EMPLYR_ID = #{emplyrId} 
   AND CMMNTY_ID = #{cmmntyId}

SELECT COUNT(A.CMMNTY_ID)
 FROM (
		SELECT CMMNTY_ID	
		  FROM COMTN_CMMNTY_USER 
		 WHERE EMPLYR_ID = #{emplyrId}
		   AND CMMNTY_ID = #{cmmntyId}
		UNION
		SELECT CMMNTY_ID	
		  FROM COMTN_CMMNTY
		 WHERE CMMNTY_ID = #{cmmntyId}
		   AND MEMBER_AT = 'N'
   	  ) A

mapper.insert:
INSERT INTO COMTN_CMMNTY_USER (
		CMMNTY_ID
	  ,	EMPLYR_ID
	  ,	SBSCRB_DE
	  ,	MNGR_AT
	  ,	USE_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
) VALUES (
		#{cmmntyId}
	  , #{emplyrId}
	  ,	sysdate()
	  ,	#{mngrAt}
	  ,	#{useAt}
	  , #{frstRegisterId}
	  , sysdate()
	  , #{frstRegisterId} 
	  , sysdate()
)

mapper.update:
UPDATE COMTN_CMMNTY_USER
   SET MNGR_AT = #{mngrAt}
     , USE_AT = #{useAt}
     , SECSN_DE = #{secsnDe}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE CMMNTY_ID = #{cmmntyId}	
   AND EMPLYR_ID = #{emplyrId}

UPDATE COMTN_CMMNTY_USER
   SET USE_AT = 'N'
     , MNGR_AT = 'N'
     , SECSN_DE = #{secsnDe}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE CMMNTY_ID = #{cmmntyId}	
   AND EMPLYR_ID = #{emplyrId}

DELETE 
  FROM COMTN_CMMNTY_USER
 WHERE CMMNTY_ID = #{cmmntyId}	
   AND EMPLYR_ID = #{emplyrId}
