mapper.select:
/* 구현 Sql */ SELECT b.ORGNZT_NM AS "orgnztNm" , a.EMPLYR_ID AS "userId" , a.USER_NM AS "emplyrNm" , a.EMPL_NO AS "emplNo" , a.OFCPS_NM AS "ofcpsNm" FROM COMTN_EMPLYR_INFO a LEFT OUTER JOIN COMTN_ORGNZT_INFO b ON a.ORGNZT_ID = b.ORGNZT_ID WHERE 1=1
AND IFNULL(b.ORGNZT_NM, ' ') LIKE CONCAT('%',#{searchKeyword},'%')
AND IFNULL(a.USER_NM, ' ') LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY b.ORGNZT_NM, a.USER_NM ASC /* 구현 Sql */ LIMIT #{recordPerPage} OFFSET #{firstIndex}

        /* 구현 Sql */
			SELECT A.SCHDUL_ID				AS "schdulId"	
				 , A.SCHDUL_NM				AS "schdulNm"
				 , A.OTHBC_SCOPE 			AS "othbcScope"
			  FROM COMTN_SCHDUL_INFO A
			 WHERE 1=1 

		   	   AND A.SCHDUL_NM LIKE CONCAT('%',#{searchKeyword},'%')

			 ORDER BY A.FRST_REGIST_PNTTM DESC
        /* 구현 Sql */
 			 LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO COMTN_SCHDUL_INFO ( 
		SCHDUL_ID
	  , SCHDUL_SE
	  , TRGET_ID
	  ,	OTHBC_SCOPE
	  ,	SCHDUL_BGNDE
	  ,	SCHDUL_ENDDE
	  ,	SCHDUL_NM
	  ,	SCHDUL_CN
	  ,	SCHDUL_PLACE
	  ,	SCHDUL_IPCR_CODE
	  ,	SCHDUL_CHARGER_ID
	  ,	ATCH_FILE_ID
	  ,	REPTIT_SE_CODE
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
)VALUES( 
		#{schdulId}
	  ,	#{schdulSe}
	  ,	#{trgetId}
	  ,	#{othbcScope}
	  ,	#{schdulBgnde}
	  ,	#{schdulEndde}
	  ,	#{schdulNm}
	  ,	#{schdulCn}
	  ,	#{schdulPlace, jdbcType=VARCHAR}
	  ,	#{schdulIpcrCode}
	  ,	#{schdulChargerId}
	  ,	#{atchFileId, jdbcType=VARCHAR}
	  ,	#{reptitSeCode}
	  , #{frstRegisterId}
	  , sysdate()
	  , #{frstRegisterId} 
	  , sysdate()
)

mapper.update:

UPDATE COMTN_SCHDUL_INFO 
   SET SCHDUL_SE			= #{schdulSe}
     , OTHBC_SCOPE			= #{othbcScope}
     , SCHDUL_BGNDE			= #{schdulBgnde}
     , SCHDUL_ENDDE			= #{schdulEndde}
     , SCHDUL_NM			= #{schdulNm}
     , SCHDUL_CN			= #{schdulCn}
     , SCHDUL_PLACE			= #{schdulPlace, jdbcType=VARCHAR}
     , SCHDUL_IPCR_CODE		= #{schdulIpcrCode}
     , SCHDUL_CHARGER_ID	= #{schdulChargerId}
     , ATCH_FILE_ID			= #{atchFileId, jdbcType=VARCHAR}
     , REPTIT_SE_CODE		= #{reptitSeCode}
     , LAST_UPDUSR_ID 		= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= sysdate()
 WHERE SCHDUL_ID = #{schdulId}
