mapper.select:
/* 구현 Sql */
SELECT a.ATCH_FILE_ID , b.FILE_CN , b.FILE_SN , b.FILE_STRE_COURS , b.STRE_FILE_NM , b.FILE_EXTSN , b.FILE_TYPE , b.ORIGNL_FILE_NM , b.FILE_SIZE , a.CREAT_DT FROM COMTN_FILE a INNER JOIN COMTN_FILE_DETAIL b ON a.ATCH_FILE_ID = b.ATCH_FILE_ID WHERE a.USE_AT = 'Y'
AND b.STRE_FILE_NM LIKE '%'|| #{searchKeyword} ||'%'
AND b.ORIGNL_FILE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY a.ATCH_FILE_ID, b.FILE_SN
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO COMTN_FILE (
		ATCH_FILE_ID
	  ,	CATEGORY
	  , CREAT_DT
	  ,	USE_AT
) VALUES ( 
		#{atchFileId}
	  ,	#{category}
	  , now()
	  ,	'Y'
)

mapper.select:

SELECT COALESCE(MAX(FILE_SN),0)+1 AS FILE_SN
  FROM COMTN_FILE_DETAIL
 WHERE ATCH_FILE_ID =  #{atchFileId}

mapper.insert:

INSERT INTO COMTN_FILE_DETAIL ( 
		ATCH_FILE_ID
	  ,	FILE_SN
	  ,	FILE_STRE_COURS
	  ,	STRE_FILE_NM
	  ,	ORIGNL_FILE_NM
	  ,	FILE_EXTSN
	  ,	FILE_TYPE
	  ,	FILE_SIZE
	  ,	FILE_CN 
) VALUES ( 
		#{atchFileId}
	  ,	#{fileSn}
	  ,	#{fileStreCours}
	  ,	#{streFileNm}
	  ,	#{orignlFileNm}
	  ,	#{fileExtsn}
	  ,	#{fileType}
	  ,	#{fileSize}
	  ,	#{fileCn}
)

mapper.update:

UPDATE COMTN_FILE
   SET USE_AT = 'N'
 WHERE ATCH_FILE_ID = #{atchFileId, jdbcType=VARCHAR}

mapper.delete:

DELETE 
  FROM COMTN_FILE_DETAIL
 WHERE ATCH_FILE_ID = #{atchFileId}
   AND FILE_SN = #{fileSn}
