mapper.select:

SELECT CACHE_GROUP
	 , CACHE_KEY
	 , CACHE_VALUE
  FROM ARMTN_CACHE_INFO
 WHERE CACHE_GROUP = #{cacheGroup}
   AND CACHE_KEY   = #{cacheKey}

mapper.insert:

INSERT INTO ARMTN_CACHE_INFO (
		CACHE_GROUP
	  , CACHE_KEY
	  ,	CACHE_VALUE
) VALUES ( 
		#{cacheGroup}
	  , #{cacheKey}
	  ,	#{cacheValue}
)

mapper.update:

UPDATE ARMTN_CACHE_INFO
   SET USE_AT = #{cacheValue}
 WHERE CACHE_GROUP = #{cacheGroup}
   AND CACHE_KEY   = #{cacheKey}
