mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT ADBK_ID AS "adbkId" , ADBK_NM AS "adbkNm" , OTHBC_SCOPE AS "othbcScope" , USE_AT AS "useAt" , WRTER_ID AS "wrterId" , FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_ADBK WHERE USE_AT = 'Y' AND (OTHBC_SCOPE = '전체' OR (OTHBC_SCOPE = '부서' AND TRGET_ORGNZT_ID = #{trgetOrgnztId}) OR (OTHBC_SCOPE = '개인' AND WRTER_ID = #{wrterId}) )
AND ADBK_NM LIKE '%'|| #{searchKeyword} ||'%'
AND OTHBC_SCOPE LIKE '%'|| #{searchKeyword} ||'%'
AND WRTER_ID LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY ADBK_NM
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

INSERT INTO COMTN_ADBK (
