	  , LAST_UPDT_PNTTM 
) VALUES ( 
		#{adbkId}
	  ,	#{adbkNm}
	  ,	#{useAt}
	  ,	#{wrterId}
	  ,	#{othbcScope}
	  ,	#{trgetOrgnztId}
	  , #{frstRegisterId}
	  , SYSDATE
	  , #{frstRegisterId} 
	  , SYSDATE
)

mapper.update:

UPDATE COMTN_ADBK 
   SET ADBK_NM = #{adbkNm}
     , USE_AT = #{useAt}
     , OTHBC_SCOPE = #{othbcScope}
	 , TRGET_ORGNZT_ID = #{trgetOrgnztId} 
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = SYSDATE
 WHERE ADBK_ID = #{adbkId}


UPDATE COMTN_ADBK 
   SET USE_AT = #{useAt}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = SYSDATE
 WHERE ADBK_ID = #{adbkId}

