mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT EMPLYR_ID AS "emplyrId" , USER_NM AS "nm" , EMAIL_ADRES AS "emailAdres" , (AREA_NO || '-' || HOUSE_MIDDLE_TELNO || '-' || HOUSE_END_TELNO) AS "homeTelno" , MBTLNUM AS "moblphonNo" , OFFM_TELNO AS "offmTelno" , FXNUM AS "fxnum" FROM COMTN_EMPLYR_INFO WHERE USER_NM LIKE '%' || #{searchKeyword} || '%' UNION SELECT ENTRPRS_MBER_ID AS "emplyrId" , CMPNY_NM AS "nm" , APPLCNT_EMAIL_ADRES AS "emailAdres" , '' AS "homeTelno" , '' AS "moblphonNo" , (AREA_NO ||'-' || ENTRPRS_MIDDLE_TELNO || '-' || ENTRPRS_END_TELNO) AS "offmTelno" , FXNUM AS "fxnum" FROM COMTN_ENTRPRS_MBER WHERE ENTRPRS_MBER_ID LIKE '%' || #{searchKeyword} || '%' UNION SELECT MBER_ID AS "emplyrId" , MBER_NM AS "nm" , MBER_EMAIL_ADRES AS "emailAdres" , (AREA_NO || '-' || MIDDLE_TELNO || '-' || END_TELNO) AS "homeTelno" , MBTLNUM AS "moblphonNo" , '' AS "offmTelno" , MBER_FXNUM AS "fxnum" FROM COMTN_GNRL_MBER WHERE MBER_ID LIKE '%' || #{searchKeyword} || '%'
ORDER BY "nm"
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT NCRD_ID AS "ncrdId" , NM AS "nm" , EMAIL_ADRES AS "emailAdres" , TELNO AS "homeTelno" , MBTLNUM AS "moblphonNo" FROM COMTN_NCRD WHERE 1=1 AND NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY NM
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}
