mapper.select:
/* 구현 Sql */
SELECT a.BBS_ID AS "bbsId" , a.NTT_ID AS "nttId" , a.NTT_SJ AS "nttSj" , a.FRST_REGISTER_ID AS "frstRegisterId" , IFNULL(b.USER_NM, a.NTCR_NM) AS "frstRegisterNm" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" , a.RDCNT AS "rdcnt" , a.THREAD_DEPTH AS "threadDepth" , a.USE_AT AS "useAt" , a.NTCR_NM AS "ntcrNm" , rtrim(a.NTCE_BGNDE) AS "ntceBgnde" , rtrim(a.NTCE_ENDDE) AS "ntceEndde" FROM COMTN_BBS a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId}
AND a.NTT_SJ LIKE '%'|| #{searchKeyword} ||'%'
AND a.NTT_CN LIKE '%'|| #{searchKeyword} ||'%'
AND b.USER_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY a.THREAD_GROUP_NO DESC, a.THREAD_NO ASC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

SELECT IFNULL(MAX(THREAD_GROUP_NO),0)+1 
  FROM COMTN_BBS
 WHERE BBS_ID = #{bbsId}


