mapper.select:

SELECT THREAD_NO 
  FROM COMTN_BBS
 WHERE BBS_ID = #{bbsId} 
   AND NTT_ID = #{parntsNttId}

mapper.update:

UPDATE COMTN_BBS 
   SET THREAD_NO = #{threadNo}
 WHERE BBS_ID = #{bbsId} 
   AND NTT_ID = #{nttId}


UPDATE COMTN_BBS 
   SET THREAD_NO = THREAD_NO + 1
 WHERE BBS_ID = #{bbsId} 
   AND THREAD_GROUP_NO = #{threadGroupNo}
   AND THREAD_NO > #{threadNo}


UPDATE COMTN_BBS 
   SET NTT_SJ = #{nttSj}
     , NTT_CN = #{nttCn, jdbcType=CLOB}
     , NTCR_ID = #{ntcrId}
     , NTCR_NM = #{ntcrNm}
     , PASSWORD = #{password}
     , NTCE_BGNDE = #{ntceBgnde}
     , NTCE_ENDDE = #{ntceEndde}
     , ATCH_FILE_ID = #{atchFileId, jdbcType=VARCHAR}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}


UPDATE COMTN_BBS 
   SET RDCNT = RDCNT+1
 WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}


UPDATE COMTN_BBS 
   SET NTT_SJ = #{nttSj}
     , USE_AT = 'N'
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}

mapper.delete:


DELETE 
  FROM COMTN_BBS 
