mapper.select:

SELECT PASSWORD
  FROM COMTN_BBS	
 WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}

/* 구현 Sql */
SELECT a.NTT_ID AS "nttId" , a.NTCR_NM AS "ntcrNm" , a.NTT_CN AS "nttCn" , a.FRST_REGISTER_ID AS "frstRegisterId" , b.USER_NM AS "frstRegisterNm" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_BBS a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #bbsId# AND a.USE_AT = 'Y' ORDER BY a.SORT_ORDR DESC, a.NTT_NO ASC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.update:

UPDATE COMTN_BBS
   SET USE_AT = 'N'
 WHERE BBS_ID = #{bbsId}
   AND NTT_ID = #{nttId}
