mapper.select:
/* 구현 Sql */
SELECT a.COMMENT_NO AS "commentNo" , a.NTT_ID AS "nttId" , a.BBS_ID AS "bbsId" , a.WRTER_ID AS "wrterId" , a.WRTER_NM AS "wrterNm" , a.COMMENT_CN AS "commentCn" , a.USE_AT AS "useAt" , b.USER_NM AS "frstRegisterNm" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_COMMENT a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId} AND a.NTT_ID = #{nttId} AND a.USE_AT = 'Y' ORDER BY a.FRST_REGIST_PNTTM ASC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

    INSERT INTO COMTN_COMMENT (
