mapper.update:

UPDATE COMTN_COMMENT 
   SET WRTER_NM = #{wrterNm}
     , PASSWORD = #{commentPassword}
     , COMMENT_CN = #{commentCn}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE COMMENT_NO = #{commentNo}


UPDATE COMTN_COMMENT
   SET USE_AT = 'N'
 WHERE COMMENT_NO = #{commentNo}

mapper.select:

