mapper.select:
/* 구현 Sql */
SELECT a.BBS_ID AS "bbsId" , a.BBS_TY_CODE AS "bbsTyCode" , b.CODE_NM AS "bbsTyCodeNm" , a.BBS_ATTRB_CODE AS "bbsAttrbCode" , c.CODE_NM AS "bbsAttrbCodeNm" , a.BBS_NM AS "bbsNm" , IFNULL(d.USE_AT, 'N') AS "useAt" , d.TRGET_ID AS "trgetId" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" , e.CMMNTY_ID AS "cmmntyId" , e.CMMNTY_NM AS "cmmntyNm" FROM COMTN_BBS_MASTER a LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM004') b ON a.BBS_TY_CODE = b.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM009') c ON a.BBS_ATTRB_CODE = c.CODE LEFT OUTER JOIN COMTN_BBS_USE d ON a.BBS_ID = d.BBS_ID LEFT OUTER JOIN COMTN_CMMNTY e ON d.TRGET_ID = e.CMMNTY_ID WHERE 1=1
AND a.BBS_NM LIKE '%'|| #{searchKeyword} ||'%'
AND b.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY d.TRGET_ID DESC, a.BBS_ID ASC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

