mapper.update:

UPDATE COMTN_BBS_MASTER 
   SET BBS_NM = #{bbsNm}
     , BBS_INTRCN = #{bbsIntrcn}
     , FILE_ATCH_POSBL_AT = #{fileAtchPosblAt}
     , ATCH_POSBL_FILE_NUMBER = #{posblAtchFileNumber}
     , ATCH_POSBL_FILE_SIZE = #{posblAtchFileSize}
     , TMPLAT_ID = #{tmplatId}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE BBS_ID = #{bbsId}


UPDATE COMTN_BBS_MASTER 
   SET USE_AT = 'N'
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE BBS_ID = #{bbsId}

mapper.select:
/* 구현 Sql */
SELECT b.BBS_ID AS "bbsId" , b.BBS_TY_CODE AS "bbsTyCode" , c.CODE_NM AS "bbsTyCodeNm" , b.BBS_ATTRB_CODE AS "bbsAttrbCode" , d.CODE_NM AS "bbsAttrbCodeNm" , b.BBS_NM AS "bbsNm" , b.TMPLAT_ID AS "tmplatId" , a.USE_AT AS "useAt" , a.PUBLIC_AT AS "publicAt" , b.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_BBS_USE a LEFT OUTER JOIN COMTN_BBS_MASTER b ON a.BBS_ID = b.BBS_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM004') c ON b.BBS_TY_CODE = c.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM009') d ON b.BBS_ATTRB_CODE = d.CODE WHERE a.TRGET_ID = #{trgetId}
AND b.BBS_NM LIKE '%'|| #{searchKeyword} ||'%'
AND c.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY b.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

SELECT COUNT(a.BBS_ID) FROM COMTN_BBS_USE a LEFT OUTER JOIN COMTN_BBS_MASTER b ON a.BBS_ID = b.BBS_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM004') c ON b.BBS_TY_CODE = c.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM009') d ON b.BBS_ATTRB_CODE = d.CODE WHERE a.TRGET_ID = #{trgetId}
AND b.BBS_NM LIKE '%'|| #{searchKeyword} ||'%'
AND c.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'

/* 구현 Sql */
SELECT a.BBS_ID AS "bbsId" , a.BBS_TY_CODE AS "bbsTyCode" , b.CODE_NM AS "bbsTyCodeNm" , a.BBS_ATTRB_CODE AS "bbsAttrbCode" , c.CODE_NM AS "bbsAttrbCodeNm" , a.BBS_NM AS "bbsNm" , a.TMPLAT_ID AS "tmplatId" , a.USE_AT AS "useAt" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_BBS_MASTER a LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM004') b ON a.BBS_TY_CODE = b.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM009') c ON a.BBS_ATTRB_CODE = c.CODE WHERE a.USE_AT = 'Y' AND a.BBS_ID NOT IN (SELECT BBS_ID FROM COMTN_BBS_USE WHERE USE_AT = 'Y')
AND a.BBS_NM LIKE '%'|| #{searchKeyword} ||'%'
AND b.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY a.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}
