mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT a.STSFDG_NO AS "stsfdgNo" , a.NTT_ID AS "nttId" , a.BBS_ID AS "bbsId" , a.WRTER_ID AS "wrterId" , a.WRTER_NM AS "wrterNm" , a.STSFDG_CN AS "stsfdgCn" , a.STSFDG AS "stsfdg" , a.USE_AT AS "useAt" , b.USER_NM AS "frstRegisterNm" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_STSFDG a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.FRST_REGISTER_ID = b.ESNTL_ID WHERE a.BBS_ID = #{bbsId} AND a.NTT_ID = #{nttId} AND a.USE_AT = 'Y' ORDER BY a.FRST_REGIST_PNTTM ASC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:


INSERT INTO COMTN_STSFDG (
		STSFDG_NO
	  ,	NTT_ID
	  ,	BBS_ID
	  , WRTER_ID
	  ,	WRTER_NM
	  ,	PASSWORD
	  ,	STSFDG_CN
	  ,	STSFDG
	  ,	USE_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
 ) VALUES ( 
 		#{stsfdgNo}
 	  ,	#{nttId}
 	  ,	#{bbsId}
 	  ,	#{wrterId}
 	  ,	#{wrterNm}
 	  , #{stsfdgPassword}
 	  ,	#{stsfdgCn}
 	  ,	#{stsfdg}
 	  ,	'Y'
	  , #{frstRegisterId}
	  , SYSDATE
	  , #{frstRegisterId} 
	  , SYSDATE
 )

mapper.update:

UPDATE COMTN_STSFDG 
   SET WRTER_NM = #{wrterNm}
     , PASSWORD = #{stsfdgPassword}
     , STSFDG_CN = #{stsfdgCn}
     , STSFDG = #{stsfdg}
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = SYSDATE
 WHERE STSFDG_NO = #{stsfdgNo}


UPDATE COMTN_STSFDG
   SET USE_AT = 'N'
 WHERE STSFDG_NO = #{stsfdgNo}

mapper.select:

SELECT PASSWORD
  FROM COMTN_STSFDG	
 WHERE STSFDG_NO = #{stsfdgNo}


SELECT NVL(AVG(STSFDG), 0)
  FROM COMTN_STSFDG 
 WHERE BBS_ID = #{bbsId} 
   AND NTT_ID = #{nttId}
   AND USE_AT = 'Y'
