mapper.select:
/* 구현 Sql */
SELECT a.BBS_ID AS "bbsId" , f.BBS_NM AS "bbsNm" , a.TRGET_ID AS "trgetId" , a.USE_AT AS "useAt" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" , c.CMMNTY_ID AS "cmmntyId" , c.CMMNTY_NM AS "cmmntyNm" FROM COMTN_BBS_USE a LEFT OUTER JOIN COMTN_CMMNTY c ON a.TRGET_ID = c.CMMNTY_ID LEFT OUTER JOIN COMTN_BBS_MASTER f ON a.BBS_ID = f.BBS_ID WHERE a.REGIST_SE_CODE != 'REGC07'
AND f.BBS_NM LIKE '%'|| #{searchKeyword} ||'%'
AND c.CMMNTY_NM LIKE '%'|| #{searchKeyword} ||'%'
AND d.CLB_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY a.TRGET_ID DESC, a.BBS_ID ASC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}


SELECT BBS_ID					AS "bbsId" 
  FROM COMTN_BBS_USE
 WHERE TRGET_ID = #{cmmntyId}


SELECT COUNT(BBS_ID)
  FROM COMTN_BBS_USE 
 WHERE BBS_ID = #{bbsId}

mapper.insert:

INSERT INTO COMTN_BBS_USE (
		BBS_ID
	  ,	TRGET_ID
	  ,	REGIST_SE_CODE
	  ,	USE_AT
	  , PUBLIC_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
) VALUES (
		#{bbsId}
	  ,	#{trgetId}
	  ,	#{registSeCode}
	  ,	#{useAt}
	  ,	#{publicAt}
	  , #{frstRegisterId}
	  , sysdate()
	  , #{frstRegisterId} 
	  , sysdate()
)

mapper.update:

	UPDATE COMTN_BBS_USE 
	   SET LAST_UPDUSR_ID = #{lastUpdusrId}
	     , LAST_UPDT_PNTTM = sysdate()


   	 	 , USE_AT = #{useAt}


   	 	 , PUBLIC_AT = #{publicAt}


	 WHERE BBS_ID = #{bbsId}


UPDATE COMTN_BBS_USE 
   SET USE_AT = 'N'
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE BBS_ID = #{bbsId}



UPDATE COMTN_BBS_USE 
   SET USE_AT = 'N'
     , LAST_UPDUSR_ID = #{lastUpdusrId}
     , LAST_UPDT_PNTTM = sysdate()
 WHERE TRGET_ID = #{cmmntyId}
