mapper.select:
/* 구현 Sql */
SELECT a.CONFM_NO AS "confmNumber" , a.CONFM_RQESTER_ID AS "confmRqesterId" , b.USER_NM AS "confmRqesterNm" , a.CONFM_DE AS "confmDe" , d.CODE_NM AS "confmTyCodeNm" , a.CONFM_STTUS_CODE AS "confmSttusCode" , e.CODE_NM AS "confmSttusCodeNm" , g.CODE_NM AS "opertTyCodeNm" FROM COMTN_CONFM_HISTORY a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.CONFM_RQESTER_ID = b.USER_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM006') d ON a.CONFM_TY_CODE = d.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM007') e ON a.CONFM_STTUS_CODE = e.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM032') g ON a.OPERT_TY_CODE = g.CODE WHERE a.CONFMER_ID = #{confmerId}
AND a.TRGET_JOB_ID = #{trgetJobId}
AND d.CODE_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND e.CODE_NM LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY a.CONFM_NO DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

SELECT COUNT(a.CONFM_NO) FROM COMTN_CONFM_HISTORY a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.CONFM_RQESTER_ID = b.USER_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM006') d ON a.CONFM_TY_CODE = d.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM007') e ON a.CONFM_STTUS_CODE = e.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM032') g ON a.OPERT_TY_CODE = g.CODE WHERE a.CONFMER_ID = #{confmerId}
AND a.TRGET_JOB_ID = #{trgetJobId}
AND d.CODE_NM LIKE CONCAT('%',#{searchKeyword},'%')
AND e.CODE_NM LIKE CONCAT('%',#{searchKeyword},'%')


SELECT COUNT(a.CONFM_NO)
  FROM COMTN_CONFM_HISTORY a	
 WHERE a.CONFM_RQESTER_ID 	= #{confmRqesterId}
   AND a.CONFM_TY_CODE 		= #{confmTyCode}
   AND a.CONFM_STTUS_CODE 	= #{confmSttusCode}
   AND a.OPERT_TY_CODE 		= #{opertTyCode}
   AND a.TRGET_JOB_TY_CODE 	= #{trgetJobTyCode}
   AND a.TRGET_JOB_ID 		= #{trgetJobId}

mapper.insert:

SELECT  IFNULL(MAX(CONFM_NO),0)+1 AS CONFM_NO  
  FROM COMTN_CONFM_HISTORY


INSERT INTO COMTN_CONFM_HISTORY (
		CONFM_NO
	  ,	CONFM_RQESTER_ID
	  ,	CONFMER_ID
	  ,	CONFM_TY_CODE
	  ,	CONFM_STTUS_CODE
	  ,	OPERT_TY_CODE
	  ,	OPERT_ID
	  ,	TRGET_JOB_TY_CODE
	  ,	TRGET_JOB_ID 
) VALUES ( 
		#{confmNumber}
	  ,	#{confmRqesterId}
	  ,	#{confmerId}
	  ,	#{confmTyCode}
	  ,	#{confmSttusCode}
	  ,	#{opertTyCode}
	  ,	#{opertId}
	  ,	#{trgetJobTyCode}
	  ,	#{trgetJobId} 
)

mapper.update:

UPDATE COMTN_CONFM_HISTORY 
   SET CONFM_DE 		= #{confmDe}
     , CONFM_STTUS_CODE = #{confmSttusCode}
 WHERE CONFM_NO = #{confmNumber}
