mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT mssage_id AS "mssageId" , (SELECT code_nm FROM COMTC_CMMN_DETAIL_CODE WHERE code_id = 'COM024' AND code = sndng_result_code) AS "sndngResultCode" , sndr AS "dsptchPerson" , rcver AS "recptnPerson" , sj AS "sj" , dsptch_dt AS "dsptchDt" , atch_file_id AS "atchFileId" FROM COMTN_EMAIL_DSPTCH_MANAGE WHERE 1=1
AND sj '%'|| #{searchVO.searchKeyword} ||'%'
AND email_cn LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND sndr LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY mssage_id DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{searchVO.firstIndex} + 1 AND #{searchVO.firstIndex} + #{searchVO.recordPerPage}

mapper.insert:

INSERT INTO COMTN_EMAIL_DSPTCH_MANAGE (	
		MSSAGE_ID
	  ,	SNDR
	  ,	RCVER
	  ,	SJ
	  ,	SNDNG_RESULT_CODE
	  ,	EMAIL_CN
	  ,	DSPTCH_DT
	  ,	ATCH_FILE_ID
) VALUES (	
		#{mssageId}
	  ,	#{dsptchPerson}
	  ,	#{recptnPerson}
	  ,	#{sj}
	  ,	#{sndngResultCode, jdbcType=VARCHAR}
	  ,	#{emailCn, jdbcType=VARCHAR}
	  ,	SYSDATE
	  ,	#{atchFileId, jdbcType=VARCHAR}
)

mapper.update:

UPDATE COMTN_EMAIL_DSPTCH_MANAGE 
   SET SNDNG_RESULT_CODE = #{sndngResultCode}
 WHERE MSSAGE_ID = #{mssageId}
