mapper.select:
/* 구현 Sql */
SELECT a.NCRD_ID AS "ncrdId" , b.NM AS "ncrdNm" , b.CMPNY_NM AS "cmpnyNm" , b.DEPT_NM AS "deptNm" , a.CREAT_DT AS "creatDt" , b.FRST_REGISTER_ID AS "frstRegisterId" FROM COMTN_NCRD_USER a LEFT OUTER JOIN COMTN_NCRD b ON a.NCRD_ID = b.NCRD_ID LEFT OUTER JOIN COMVN_USER_MASTER c ON a.EMPLYR_ID = c.ESNTL_ID WHERE a.USE_AT = 'Y' AND a.EMPLYR_ID = #{emplyrId}
AND NM LIKE '%'|| #{searchKeyword} ||'%'
AND CMPNY_NM LIKE '%'|| #{searchKeyword} ||'%'
AND DEPT_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY NM
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

