mapper.update:

UPDATE COMTN_NCRD 
   SET NM 				= #{ncrdNm}
     , OTHBC_SCOPE 		= #{othbcScope}
     , CLSF_NM 			= #{clsfNm}
     , OFCPS_NM 		= #{ofcpsNm}
     , CMPNY_NM 		= #{cmpnyNm}
     , DEPT_NM 			= #{deptNm}
     , EMAIL_ADRES 		= #{emailAdres}
     , TELNO 			= #{telNo}
     , NATION_NO 		= #{nationNo}
     , AREA_NO 			= #{areaNo}
     , MIDDLE_TELNO 	= #{middleTelNo}
     , END_TELNO 		= #{endTelNo}
     , MBTLNUM 			= #{mbtlNum}
     , IDNTFC_NO 		= #{idntfcNo}
     , MIDDLE_MBTLNUM 	= #{middleMbtlNum}
     , END_MBTLNUM 		= #{endMbtlNum}
     , ADRES 			= #{adres}
     , DETAIL_ADRES 	= #{detailAdres}
     , ZIP 				= #{zip}
     , RM 				= #{remark}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= now()
 WHERE NCRD_ID = #{ncrdId}

mapper.select:
/* 구현 Sql */
SELECT a.NCRD_ID AS "ncrdId" , a.NM AS "ncrdNm" , a.CMPNY_NM AS "cmpnyNm" , a.DEPT_NM AS "deptNm" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" , b.EMPLYR_ID AS "emplyrId" FROM COMTN_NCRD a LEFT OUTER JOIN (SELECT NCRD_ID, EMPLYR_ID FROM COMTN_NCRD_USER WHERE EMPLYR_ID = #{emplyrId}) b ON a.NCRD_ID = b.NCRD_ID WHERE (a.OTHBC_SCOPE = 'G' OR (a.OTHBC_SCOPE = 'C' AND a.TRGET_ID = #{trgetId}) )
AND a.NM LIKE '%'|| #{searchKeyword} ||'%'
AND a.CMPNY_NM LIKE '%'|| #{searchKeyword} ||'%'
AND a.DEPT_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY NM
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO COMTN_NCRD_USER	(
