	  ,	USE_AT
) VALUES (
		#{ncrdId}
	  ,	#{emplyrId}
	  ,	now()
	  ,	#{registSeCode}
	  , #{useAt}
)

mapper.delete:

DELETE 
  FROM COMTN_NCRD_USER 
 WHERE NCRD_ID = #{ncrdId}
   AND EMPLYR_ID = #{emplyrId}
