mapper.select:
/* 구현 Sql */
SELECT a.SCRAP_ID AS "scrapId" , a.SCRAP_NM AS "scrapNm" , b.USER_NM AS "frstRegisterNm" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_SCRAP a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.FRST_REGISTER_ID = b.USER_ID WHERE a.USE_AT = 'Y' AND a.FRST_REGISTER_ID = #{userId}
AND a.SCRAP_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY a.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO COMTN_SCRAP (
		SCRAP_ID
	  ,	NTT_ID
	  ,	BBS_ID
	  ,	SCRAP_NM
	  ,	USE_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
 ) VALUES ( 
 		#{scrapId}
 	  ,	#{nttId}
 	  ,	#{bbsId}
 	  ,	#{scrapNm}
 	  ,	'Y'
	  , #{frstRegisterId}
	  , now()
	  , #{frstRegisterId} 
	  , now()
 )

mapper.update:

UPDATE COMTN_SCRAP 
   SET SCRAP_NM 		= #{scrapNm}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= now()
 WHERE SCRAP_ID = #{scrapId}


UPDATE COMTN_SCRAP
   SET USE_AT = 'N'
 WHERE SCRAP_ID = #{scrapId}
