mapper.select:
/* 구현 Sql */
SELECT a.SMS_ID AS "smsId" , a.TRNSMIS_TELNO AS "trnsmitTelno" , (SELECT COUNT(*) FROM COMTN_SMS_RECPTN s WHERE s.SMS_ID = a.SMS_ID) AS "recptnCnt" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_SMS a WHERE 1=1
AND a.SMS_ID in (SELECT SMS_ID FROM COMTN_SMS_RECPTN WHERE RECPTN_TELNO LIKE '%' || #{searchKeyword} || '%')
AND a.TRNSMIS_CN LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY a.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO COMTN_SMS (
		SMS_ID
	  ,	TRNSMIS_TELNO
	  ,	TRNSMIS_CN
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
) VALUES ( 
		#{smsId}
	  ,	#{trnsmitTelno}
	  ,	#{trnsmitCn}
	  , #{frstRegisterId}
	  , now()
)


INSERT INTO COMTN_SMS_RECPTN (
		SMS_ID
	  ,	RECPTN_TELNO
	  ,	RESULT_CODE
	  ,	RESULT_MSSAGE 
) VALUES ( 
		#{smsId}
	  ,	#{recptnTelno}
	  , #{resultCode}
	  ,	#{resultMssage}
)


UPDATE COMTN_SMS_RECPTN 
   SET RESULT_CODE = #{resultCode}
     , RESULT_MSSAGE = #{resultMssage}
 WHERE SMS_ID = #{smsId} 
   AND RECPTN_TELNO = #{recptnTelno}
