mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.DIARY_ID AS "diaryId" , A.DIARY_NM AS "diaryNm" , (SELECT SCHDUL_NM FROM COMTN_SCHDUL_INFO WHERE SCHDUL_ID = A.SCHDUL_ID ) AS "schdulNm" , A.DIARY_PROGRSRT AS "diaryProcsPte" , A.FRST_REGIST_PNTTM AS "frstRegisterPnttm" , (SELECT USER_NM FROM COMTN_EMPLYR_INFO WHERE USER_ID = A.FRST_REGISTER_ID ) AS "frstRegisterNm" FROM COMTN_DIARY_INFO A WHERE 1=1
AND A.DIARY_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.DRCT_MATTER LIKE '%'|| #{searchKeyword} ||'%'
AND A.PARTCLR_MATTER LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

INSERT INTO COMTN_DIARY_INFO ( 
		DIARY_ID
	  , SCHDUL_ID
	  ,	DIARY_PROGRSRT
	  ,	DIARY_NM
	  ,	DRCT_MATTER
	  ,	PARTCLR_MATTER
	  ,	ATCH_FILE_ID
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
)VALUES( 
		#{diaryId}
	  ,	#{schdulId}
	  ,	#{diaryProcsPte}
	  ,	#{diaryNm}
	  ,	#{drctMatter}
	  ,	#{partclrMatter}
	  ,	#{atchFileId, jdbcType=VARCHAR}
	  , #{frstRegisterId}
	  , SYSDATE
	  , #{frstRegisterId} 
	  , SYSDATE
)

mapper.update:

UPDATE COMTN_DIARY_INFO 
   SET DIARY_PROGRSRT	= #{diaryProcsPte}
     , DIARY_NM			= #{diaryNm}
     , DRCT_MATTER		= #{drctMatter}
     , PARTCLR_MATTER	= #{partclrMatter}
     , ATCH_FILE_ID		= #{atchFileId, jdbcType=VARCHAR}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM = SYSDATE
 WHERE DIARY_ID = #{diaryId}
