mapper.select:
/* 구현 Sql */
SELECT a.TMPLAT_ID AS "tmplatId" , a.TMPLAT_NM AS "tmplatNm" , c.CODE_NM AS "tmplatSeCodeNm" , a.TMPLAT_COURS AS "tmplatCours" , a.USE_AT AS "useAt" , a.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_TMPLAT_INFO a LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM005') c ON a.TMPLAT_SE_CODE = c.CODE WHERE 1=1
AND a.TMPLAT_SE_CODE = #{tmplatSeCode}
AND a.TMPLAT_NM LIKE '%'|| #{searchKeyword} ||'%'
AND c.CODE_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY a.FRST_REGIST_PNTTM DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO COMTN_TMPLAT_INFO (
		TMPLAT_ID
	  , TMPLAT_NM
	  ,	TMPLAT_SE_CODE
	  ,	TMPLAT_COURS
	  ,	USE_AT
	  , FRST_REGISTER_ID 
	  , FRST_REGIST_PNTTM 
	  , LAST_UPDUSR_ID
	  , LAST_UPDT_PNTTM 
) VALUES ( 
		#{tmplatId}
	  ,	#{tmplatNm}
	  ,	#{tmplatSeCode}
	  ,	#{tmplatCours}
	  ,	#{useAt}
	  , #{frstRegisterId}
	  , sysdate()
	  , #{frstRegisterId} 
	  , sysdate()
)

mapper.update:

UPDATE COMTN_TMPLAT_INFO 
   SET TMPLAT_SE_CODE 	= #{tmplatSeCode}
     , TMPLAT_COURS 	= #{tmplatCours}
     , USE_AT 			= #{useAt}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= sysdate()
 WHERE TMPLAT_ID = #{tmplatId}
