mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT C.ORGNZT_NM AS "orgnztNm" , B.KNWLDG_TY_CODE AS "knoTypeCd" , B.KNWLDG_TY_NM AS "knoTypeNm" , D.USER_NM AS "userNm" , A.EXPERT_GRAD AS "appTypeCd" , A.EXPERT_CONFM_DE AS "speConfmDe" , A.EXPERT_ID AS "speId" FROM COMTN_DAM_PRO A LEFT OUTER JOIN COMTN_DAM_MAP_KNO B ON A.KNWLDG_TY_CODE = B.KNWLDG_TY_CODE LEFT OUTER JOIN COMTN_DAM_MAP_TEAM C ON B.ORGNZT_ID = C.ORGNZT_ID LEFT OUTER JOIN COMVN_USER_MASTER D ON A.EXPERT_ID = D.ESNTL_ID WHERE 1=1
AND D.USER_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND B.KNWLDG_TY_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{searchVO.firstIndex} + 1 AND #{searchVO.firstIndex} + #{searchVO.recordPerPage}

mapper.insert:


    INSERT INTO COMTN_DAM_PRO ( 
    	   EXPERT_ID
         , KNWLDG_TY_CODE
         , EXPERT_GRAD
         , EXPERT_DC
         , EXPERT_CONFM_DE
		 , FRST_REGISTER_ID 
		 , FRST_REGIST_PNTTM 
		 , LAST_UPDUSR_ID
		 , LAST_UPDT_PNTTM 
    ) VALUES ( 
    	   #{speId}         		         
         , #{knoTypeCd}
         , #{appTypeCd}
         , #{speExpCn}
         , #{speConfmDe}        		         
		 , #{frstRegisterId}
		 , SYSDATE
		 , #{frstRegisterId} 
		 , SYSDATE
    )

mapper.update:

UPDATE COMTN_DAM_PRO 
   SET EXPERT_GRAD      	= #{appTypeNm}
     , EXPERT_DC	      	= #{speExpCn} 
     , EXPERT_CONFM_DE	 	= #{speConfmDe}                            
     , LAST_UPDUSR_ID    	= #{lastUpdusrId}               
     , LAST_UPDT_PNTTM 		= SYSDATE
 WHERE EXPERT_ID       	  	= #{speId}
   AND KNWLDG_TY_CODE 		= #{knoTypeCd}
   AND EXPERT_GRAD		  	= #{appTypeCd}

mapper.delete:

DELETE 
  FROM COMTN_DAM_PRO 
 WHERE EXPERT_ID 		= #{speId}
   AND KNWLDG_TY_CODE 	= #{knoTypeCd}
   AND EXPERT_GRAD 		= #{appTypeCd}
