mapper.select:
/* 구현 Sql */
SELECT A.RESOURCE_CODE AS "resourceCode" , A.RESOURCE_NM AS "resourceNm" , A.RESOURCE_DC AS "resourceDc" , A.RESOURCE_TY AS "resourceTy" , A.RESOURCE_SORT AS "resourceSort" , B.CREAT_DT AS "creatDt" , (CASE WHEN B.RESOURCE_CODE IS NULL THEN 'N' ELSE 'Y' END) AS "regYn" FROM COMTN_RESOURCE_INFO A LEFT OUTER JOIN (SELECT RESOURCE_CODE, CREAT_DT FROM COMTN_AUTHOR_RESOURCE WHERE AUTHOR_CODE = #{authorCode}) B ON A.RESOURCE_CODE = B.RESOURCE_CODE
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO COMTN_AUTHOR_RESOURCE ( 
		AUTHOR_CODE
	  ,	RESOURCE_CODE
	  ,	CREAT_DT
) VALUES ( 
		#{authorCode}
	  ,	#{resourceCode}
	  ,	now()
)

mapper.delete:

DELETE 
  FROM COMTN_AUTHOR_RESOURCE 
 WHERE AUTHOR_CODE=#{authorCode}
   AND RESOURCE_CODE=#{resourceCode}
