mapper.select:
/* 구현 Sql */
SELECT AUTHOR_CODE , AUTHOR_NM , AUTHOR_DC , AUTHOR_CREAT_DE FROM COMTN_AUTHOR_INFO WHERE 1=1
AND AUTHOR_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY AUTHOR_CREAT_DE DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:

INSERT INTO COMTN_AUTHOR_INFO ( 
		AUTHOR_CODE
	  ,	AUTHOR_NM
	  ,	AUTHOR_DC
	  ,	AUTHOR_CREAT_DE 
) VALUES ( 
		#{authorCode}
	  ,	#{authorNm}
	  ,	#{authorDc}
	  ,	now()
)

mapper.update:

UPDATE COMTN_AUTHOR_INFO 
   SET AUTHOR_NM=#{authorNm}
     , AUTHOR_DC=#{authorDc}
 WHERE AUTHOR_CODE=#{authorCode}
