mapper.select:
       /* 구현 Sql */

           SELECT ORGNZT_ID 				AS "deptCode" 
                , ORGNZT_NM 				AS "deptNm" 
             FROM COMTN_ORGNZT_INFO             
            WHERE 1 = 1 


               AND ORGNZT_NM LIKE '%'|| #{searchKeyword} ||'%'  

       /* 구현 Sql */

			LIMIT  #{recordPerPage} OFFSET #{firstIndex}

/* 구현 Sql */
SELECT A.USER_ID AS "userId" , A.USER_NM AS "userNm" , B.AUTHOR_CODE AS "authorCode" , (CASE WHEN B.SCRTY_DTRMN_TRGET_ID IS NULL THEN 'N' ELSE 'Y' END) AS "regYn" FROM (SELECT A.ORGNZT_ID DEPT_CODE , A.ORGNZT_NM DEPT_NM , B.EMPLYR_ID USER_ID , B.USER_NM USER_NM FROM COMTN_ORGNZT_INFO A LEFT OUTER JOIN COMTN_EMPLYR_INFO B ON A.ORGNZT_ID = B.ORGNZT_ID ) A LEFT OUTER JOIN COMTN_EMPLYR_SCRTY_ESTBS B ON A.USER_ID = B.SCRTY_DTRMN_TRGET_ID WHERE 1 = 1
AND A.DEPT_CODE = #{deptCode}
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

INSERT INTO COMTN_EMPLYR_SCRTY_ESTBS ( 
		SCRTY_DTRMN_TRGET_ID
	  ,	AUTHOR_CODE
) VALUES ( 
		#{userId}
	  ,	#{authorCode}
)

mapper.update:

UPDATE COMTN_EMPLYR_SCRTY_ESTBS 
   SET AUTHOR_CODE=#{authorCode}
 WHERE SCRTY_DTRMN_TRGET_ID=#{userId}
