mapper.select:

    SELECT *
      FROM (
    SELECT ROWNUM RNUM, ALL_LIST.*
      FROM (

/* 구현 Sql */

    SELECT ORGNZT_ID				
         , ORGNZT_NM				
         , ORGNZT_DC				
      FROM COMTN_ORGNZT_INFO 
     WHERE 1=1


		 AND ORGNZT_NM LIKE '%'|| #{searchKeyword} ||'%'   


     ORDER BY ORGNZT_ID DESC

/* 구현 Sql */

           ) ALL_LIST
           )
     WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

INSERT INTO COMTN_ORGNZT_INFO (
		ORGNZT_ID
	  , ORGNZT_NM
	  , ORGNZT_DC
) VALUES (
		#{orgnztId}
	  , #{orgnztNm}
	  , #{orgnztDc}
)

mapper.update:

UPDATE COMTN_ORGNZT_INFO
   SET ORGNZT_NM = #{orgnztNm}
     , ORGNZT_DC = #{orgnztDc}  
 WHERE ORGNZT_ID = #{orgnztId}
