mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.USER_ID AS "userId" , A.USER_NM AS "userNm" , A.MBER_TY_CODE AS "mberTyCode" , (SELECT CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM012' AND CODE = A.MBER_TY_CODE ) AS "mberTyNm" , B.AUTHOR_CODE AS "authorCode" , (CASE WHEN B.SCRTY_DTRMN_TRGET_ID IS NULL THEN 'N' ELSE 'Y' END) AS "regYn" , ESNTL_ID AS "uniqId" FROM (SELECT MBER_ID USER_ID , MBER_NM USER_NM , GROUP_ID , 'USR01' MBER_TY_CODE , ESNTL_ID FROM COMTN_GNRL_MBER UNION ALL SELECT ENTRPRS_MBER_ID USER_ID , CMPNY_NM USER_NM , GROUP_ID , 'USR02' MBER_TY_CODE , ESNTL_ID FROM COMTN_ENTRPRS_MBER UNION ALL SELECT EMPLYR_ID USER_ID , USER_NM USER_NM , GROUP_ID , 'USR03' MBER_TY_CODE , ESNTL_ID FROM COMTN_EMPLYR_INFO ) A LEFT OUTER JOIN COMTN_EMPLYR_SCRTY_ESTBS B ON A.ESNTL_ID = B.SCRTY_DTRMN_TRGET_ID WHERE 1 = 1
AND A.USER_ID LIKE '%'|| #{searchKeyword} ||'%'
AND A.USER_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.GROUP_ID = #{searchKeyword}
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

INSERT INTO COMTN_EMPLYR_SCRTY_ESTBS ( 
		SCRTY_DTRMN_TRGET_ID
	  ,	MBER_TY_CODE
	  ,	AUTHOR_CODE
) VALUES ( 
		#{uniqId}
	  ,	#{mberTyCode}
	  , #{authorCode}
)

mapper.update:

UPDATE COMTN_EMPLYR_SCRTY_ESTBS 
   SET MBER_TY_CODE=#{mberTyCode}
     , AUTHOR_CODE=#{authorCode}
 WHERE SCRTY_DTRMN_TRGET_ID=#{uniqId}
