mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT GROUP_ID , GROUP_NM , GROUP_DC , GROUP_CREAT_DE FROM COMTN_AUTHOR_GROUP_INFO WHERE 1=1
AND GROUP_NM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY GROUP_CREAT_DE DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

 INSERT INTO COMTN_AUTHOR_GROUP_INFO ( 
 		GROUP_ID
 	  ,	GROUP_NM
 	  ,	GROUP_DC
 	  ,	GROUP_CREAT_DE 
	) VALUES ( 
			#{groupId}
		  ,	#{groupNm}
		  ,	#{groupDc}
		  ,	SYSDATE
	)

mapper.update:

UPDATE COMTN_AUTHOR_GROUP_INFO 
   SET GROUP_NM=#{groupNm}
     , GROUP_DC=#{groupDc}
 WHERE GROUP_ID=#{groupId}
