mapper.select:
/* 구현 Sql */
SELECT RESOURCE_CODE AS "resourceCode" , RESOURCE_NM AS "resourceNm" , RESOURCE_DC AS "resourceDc" , (SELECT CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM029' AND CODE = RESOURCE_TY ) AS "resourceTy" , RESOURCE_SORT AS "resourceSort" , RESOURCE_CREAT_DE AS "resourceCreatDe" FROM COMTN_RESOURCE_INFO WHERE 1=1
AND RESOURCE_NM LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY RESOURCE_CREAT_DE DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

 INSERT INTO COMTN_RESOURCE_INFO ( 
 		RESOURCE_CODE
 	  ,	RESOURCE_NM
 	  ,	RESOURCE_PTTRN
 	  ,	RESOURCE_DC
 	  ,	RESOURCE_TY
 	  , RESOURCE_SORT
 	  ,	RESOURCE_CREAT_DE 
) VALUES ( 
			#{resourceCode}
		  ,	#{resourceNm}
		  ,	#{resourcePttrn}
		  ,	#{resourceDc}
		  , #{resourceTy}
		  ,	#{resourceSort}
		  , sysdate()
)

mapper.update:

UPDATE COMTN_RESOURCE_INFO 
   SET RESOURCE_NM		= #{resourceNm}
     , RESOURCE_PTTRN	= #{resourcePttrn}
     , RESOURCE_DC		= #{resourceDc}
     , RESOURCE_TY		= #{resourceTy}
     , RESOURCE_SORT	= #{resourceSort}
     , RESOURCE_CREAT_DE= SYSDATE
 WHERE RESOURCE_CODE	= #{resourceCode}
