mapper.select:
SELECT a.OCCRRNC_DE AS statsDate , a.TOP_INQIRE_BBSCTT_ID AS mxmmInqireBbsId , b.NTT_SJ AS mxmmInqireBbsNm , b.RDCNT AS maxStatsCo FROM ( SELECT OCCRRNC_DE , TOP_INQIRE_BBSCTT_ID FROM COMTS_BBS_SUMMARY WHERE 1 = 1 AND OCCRRNC_DE BETWEEN #{fromDate} AND #{toDate} AND STATS_SE = #{statsKind}
AND DETAIL_STATS_SE = #{detailStatsKind}
) a , COMTN_BBS b WHERE a.TOP_INQIRE_BBSCTT_ID = b.NTT_ID ORDER BY a.OCCRRNC_DE

SELECT a.OCCRRNC_DE AS statsDate , a.MUMM_INQIRE_BBSCTT_ID AS mummInqireBbsId , b.NTT_SJ AS mummInqireBbsNm , b.RDCNT AS minStatsCo FROM ( SELECT OCCRRNC_DE , MUMM_INQIRE_BBSCTT_ID FROM COMTS_BBS_SUMMARY WHERE 1 = 1 AND OCCRRNC_DE BETWEEN #{fromDate} AND #{toDate} AND STATS_SE = #{statsKind}
AND DETAIL_STATS_SE = #{detailStatsKind}
) a , COMTN_BBS b WHERE a.MUMM_INQIRE_BBSCTT_ID = b.NTT_ID ORDER BY a.OCCRRNC_DE

SELECT a.OCCRRNC_DE AS statsDate , a.TOP_NTCR_ID AS topNtcepersonId , COUNT(b.NTT_ID) AS topNtcepersonCo FROM ( SELECT OCCRRNC_DE , TOP_NTCR_ID FROM COMTS_BBS_SUMMARY WHERE 1 = 1 AND OCCRRNC_DE BETWEEN #{fromDate} AND #{toDate} AND STATS_SE = #{statsKind}
AND DETAIL_STATS_SE = #{detailStatsKind}
) a , COMTN_BBS b WHERE 1=1 AND a.TOP_NTCR_ID IS NOT NULL AND b.NTCR_ID = a.TOP_NTCR_ID AND b.NTCR_ID IS NOT NULL GROUP BY a.OCCRRNC_DE, a.TOP_NTCR_ID ORDER BY a.OCCRRNC_DE


SELECT STATS_SE 		AS statsKind
  FROM COMTS_BBS_SUMMARY 
 WHERE RTRIM(OCCRRNC_DE) = TO_CHAR(SYSDATE - 1, 'YYYYMMDD')
   AND STATS_SE = #{statsKind}
   AND DETAIL_STATS_SE = #{detailStatsKind}

mapper.delete:

DELETE 
  FROM COMTS_BBS_SUMMARY
 WHERE RTRIM(OCCRRNC_DE) = TO_CHAR(SYSDATE - 1, 'YYYYMMDD')
   AND STATS_SE = #{statsKind}
   AND DETAIL_STATS_SE = #{detailStatsKind}

mapper.insert:

INSERT INTO COMTS_BBS_SUMMARY (
		OCCRRNC_DE
	  , STATS_SE
	  ,	DETAIL_STATS_SE
	  ,	CREAT_CO
	  , TOT_RDCNT
	  ,	AVRG_RDCNT
	  ,	TOP_INQIRE_BBSCTT_ID
	  ,	MUMM_INQIRE_BBSCTT_ID
	  ,	TOP_NTCR_ID
) VALUES (
		TO_CHAR(SYSDATE - 1, 'YYYYMMDD')
	  ,	#{statsKind}
	  ,	#{detailStatsKind}
	  ,	#{creatCo}
	  ,	#{totInqireCo}
	  ,	#{avrgInqireCo}
	  ,	#{mxmmInqireBbsId}
	  ,	#{mummInqireBbsId}
	  ,	#{topNtcepersonId}
)
