mapper.select:
/* 구현 Sql */
SELECT A.BATCH_OPERT_ID AS "batchOpertId" , A.BATCH_OPERT_NM AS "batchOpertNm" , A.BATCH_PROGRM AS "batchProgrm" , A.PARAMTR AS "paramtr" , A.BATCH_OBJECT AS "batchObject" , A.BATCH_METHOD AS "batchMethod" , A.BATCH_BEAN AS "batchBean" FROM COMTN_BATCH_OPERT A WHERE USE_AT = 'Y'
AND A.BATCH_OPERT_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.BATCH_PROGRM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.BATCH_OPERT_ID ASC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

            INSERT INTO COMTN_BATCH_OPERT (
	                BATCH_OPERT_ID 
	              , BATCH_OPERT_NM
	              , BATCH_PROGRM
	              , PARAMTR
	              , BATCH_OBJECT
	              , BATCH_METHOD
	              , BATCH_BEAN
	              , USE_AT
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
	                #{batchOpertId}
	              , #{batchOpertNm}
	              , #{batchProgrm}
	              , #{paramtr}
	              , #{batchObject}
	              , #{batchMethod}
	              , #{batchBean}
	              , 'Y'
				  , #{frstRegisterId}
				  , sysdate()
				  , #{frstRegisterId} 
				  , sysdate()
            )

mapper.update:

UPDATE COMTN_BATCH_OPERT 
   SET BATCH_OPERT_NM 	= #{batchOpertNm}
     , BATCH_PROGRM 	= #{batchProgrm}
     , PARAMTR 			= #{paramtr}
     , BATCH_OBJECT 	= #{batchObject}
     , BATCH_METHOD 	= #{batchMethod}
     , BATCH_BEAN 		= #{batchBean}
     , LAST_UPDUSR_ID 	= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	= sysdate()
 WHERE BATCH_OPERT_ID 	= #{batchOpertId}

mapper.delete:

UPDATE COMTN_BATCH_OPERT 
   SET USE_AT = 'N'
 WHERE BATCH_OPERT_ID = #{batchOpertId}
