mapper.select:
/* 구현 Sql */
SELECT A.BATCH_RESULT_ID , A.BATCH_SCHDUL_ID , A.BATCH_OPERT_ID , B.BATCH_OPERT_NM , B.BATCH_PROGRM , B.PARAMTR , B.BATCH_OBJECT , B.BATCH_METHOD , A.STTUS , C.CODE_NM AS "sttusNm" , A.ERROR_INFO , A.EXECUT_BEGIN_TM AS "executBeginTime" , A.EXECUT_END_TM AS "executEndTime" , A.LAST_UPDT_PNTTM AS "lastUpdusrPnttm" , A.LAST_UPDUSR_ID , A.FRST_REGISTER_ID , A.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_BATCH_RESULT A LEFT OUTER JOIN COMTN_BATCH_OPERT B ON A.BATCH_OPERT_ID = B.BATCH_OPERT_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM076') C ON A.STTUS = C.CODE WHERE 1=1
AND A.STTUS = #{sttus}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) >= #{searchKeywordFrom}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) <= #{searchKeywordTo}
AND B.BATCH_OPERT_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.BATCH_SCHDUL_ID LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.BATCH_RESULT_ID DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTN_BATCH_RESULT A LEFT OUTER JOIN COMTN_BATCH_OPERT B ON A.BATCH_OPERT_ID = B.BATCH_OPERT_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM076') C ON A.STTUS = C.CODE WHERE 1=1
AND A.STTUS = #{sttus}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) >= #{searchKeywordFrom}
AND SUBSTR(A.EXECUT_BEGIN_TM,1, 8) <= #{searchKeywordTo}
AND B.BATCH_OPERT_NM LIKE '%'|| #{searchKeyword} ||'%'
AND A.BATCH_SCHDUL_ID LIKE '%'|| #{searchKeyword} ||'%'

mapper.insert:

            INSERT INTO COMTN_BATCH_RESULT (
                	BATCH_RESULT_ID
                  ,	BATCH_SCHDUL_ID
                  ,	BATCH_OPERT_ID
                  ,	STTUS
                  ,	ERROR_INFO
                  ,	EXECUT_BEGIN_TM
                  ,	EXECUT_END_TM
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
                    #{batchResultId}
                  , #{batchSchdulId}
                  , #{batchOpertId}
                  , #{sttus, jdbcType=VARCHAR}
                  , #{errorInfo, jdbcType=VARCHAR}
                  , #{executBeginTime, jdbcType=VARCHAR}
                  , #{executEndTime, jdbcType=VARCHAR}
				  , #{frstRegisterId}
				  , now()
				  , #{frstRegisterId} 
				  , now()
            )

mapper.update:

UPDATE COMTN_BATCH_RESULT 
   SET ERROR_INFO 		= #{errorInfo, jdbcType=VARCHAR}
   	 , STTUS 			= #{sttus, jdbcType=VARCHAR}
   	 , EXECUT_END_TM 	= #{executEndTime, jdbcType=VARCHAR}
   	 , LAST_UPDUSR_ID 	= #{lastUpdusrId}
   	 , LAST_UPDT_PNTTM 	= now()
 WHERE BATCH_RESULT_ID 	= #{batchResultId}
