mapper.select:
/* 구현 Sql */
SELECT A.BATCH_SCHDUL_ID , A.BATCH_OPERT_ID , A.START_DELAY , A.REPEAT_INTERVAL , A.EXECUT_CYCLE , C.CODE_NM AS "executCycleNm" , A.EXECUT_SCHDUL_DE , A.EXECUT_SCHDUL_HOUR , A.EXECUT_SCHDUL_MNT , A.EXECUT_SCHDUL_SECND , B.BATCH_OPERT_NM , B.BATCH_PROGRM , B.PARAMTR , B.BATCH_OBJECT , B.BATCH_METHOD , A.LAST_UPDT_PNTTM AS "lastUpdusrPnttm" , A.LAST_UPDUSR_ID , A.FRST_REGISTER_ID , A.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_BATCH_SCHDUL A LEFT OUTER JOIN COMTN_BATCH_OPERT B ON A.BATCH_OPERT_ID = B.BATCH_OPERT_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM047') C ON A.EXECUT_CYCLE = C.CODE WHERE 1=1
AND B.BATCH_OPERT_NM LIKE '%'|| #{searchKeyword} ||'%'
AND B.BATCH_PROGRM LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY A.BATCH_SCHDUL_ID ASC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

            INSERT INTO COMTN_BATCH_SCHDUL (
                	BATCH_SCHDUL_ID
                  ,	BATCH_OPERT_ID
           		  , START_DELAY
           		  , REPEAT_INTERVAL
                  ,	EXECUT_CYCLE
                  ,	EXECUT_SCHDUL_DE
                  ,	EXECUT_SCHDUL_HOUR
                  ,	EXECUT_SCHDUL_MNT
                  ,	EXECUT_SCHDUL_SECND
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
                    #{batchSchdulId}
                  , #{batchOpertId}
           		  , #{startDelay}
           		  , #{repeatInterval}
                  , #{executCycle}
                  , #{executSchdulDe}
                  , #{executSchdulHour}
                  , #{executSchdulMnt}
                  , #{executSchdulSecnd}
				  , #{frstRegisterId}
				  , sysdate()
				  , #{frstRegisterId} 
				  , sysdate()
             )

mapper.update:

 UPDATE COMTN_BATCH_SCHDUL 
    SET BATCH_OPERT_ID 		= #{batchOpertId}
		 , START_DELAY			= #{startDelay}
		 , REPEAT_INTERVAL		= #{repeatInterval}
    	 , EXECUT_CYCLE 		= #{executCycle}
    	 , EXECUT_SCHDUL_DE 	= #{executSchdulDe}
    	 , EXECUT_SCHDUL_HOUR 	= #{executSchdulHour}
    	 , EXECUT_SCHDUL_MNT 	= #{executSchdulMnt}
    	 , EXECUT_SCHDUL_SECND 	= #{executSchdulSecnd}
    	 , LAST_UPDUSR_ID 		= #{lastUpdusrId}
    	 , LAST_UPDT_PNTTM 		= sysdate()
  WHERE BATCH_SCHDUL_ID = #{batchSchdulId}

mapper.insert:

INSERT INTO COMTN_BATCH_SCHDUL_DFK (
