mapper.select:
/* 구현 Sql */
SELECT A.RESTDE_NO AS "restdeNo" , A.RESTDE AS "restdeDe" , A.RESTDE_NM AS "restdeNm" , B.CODE_NM AS "restdeSe" FROM COMTN_RESTDE A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM017') B ON A.RESTDE_SE_CODE = B.CODE WHERE 1=1
AND A.RESTDE = #{searchKeyword}
AND A.RESTDE_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY RESTDE DESC
/* 구현 Sql */
LIMIT #{recordPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTN_RESTDE A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM017') B ON A.RESTDE_SE_CODE = B.CODE WHERE 1=1
AND A.RESTDE = #{searchKeyword}
AND A.RESTDE_NM LIKE '%' || #{searchKeyword} || '%'

