mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT B.CL_CODE_NM AS "clCodeNm" , A.CODE_ID AS "codeId" , A.CODE_ID_NM AS "codeIdNm" , A.USE_AT AS "useAt" FROM COMTC_CMMN_CODE A LEFT OUTER JOIN COMTC_CMMN_CL_CODE B ON A.CL_CODE = B.CL_CODE WHERE 1=1
AND A.CODE_ID LIKE '%' || #{searchKeyword} || '%'
AND A.CODE_ID_NM LIKE '%' || #{searchKeyword} || '%'
AND A.USE_AT = 'Y' AND A.CL_CODE LIKE '%' || #{searchKeyword} || '%'
ORDER BY A.CODE_ID ASC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

    INSERT INTO  COMTC_CMMN_CODE (  
    	   CODE_ID
         , CODE_ID_NM
         , CODE_ID_DC
         , CL_CODE
         , USE_AT
		 , FRST_REGISTER_ID 
		 , FRST_REGIST_PNTTM 
		 , LAST_UPDUSR_ID
		 , LAST_UPDT_PNTTM 
    ) VALUES (  
    	   #{codeId}
         , #{codeIdNm}
         , #{codeIdDc}
         , #{clCode}
         , #{useAt}
		 , #{frstRegisterId}
		 , SYSDATE
		 , #{frstRegisterId}
		 , SYSDATE
    )

mapper.update:

UPDATE COMTC_CMMN_CODE
   SET CODE_ID_NM        = #{codeIdNm}
     , CODE_ID_DC        = #{codeIdDc}
     , USE_AT            = #{useAt}
     , LAST_UPDUSR_ID    = #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	 = SYSDATE
 WHERE CODE_ID           = #{codeId}

mapper.delete:

UPDATE COMTC_CMMN_CODE
   SET USE_AT  = 'N'
 WHERE CODE_ID = #{codeId}
