mapper.select:

            SELECT *
              FROM (
            SELECT ROWNUM RNUM, ALL_LIST.*
              FROM (

        /* 구현 Sql */

			SELECT CL_CODE					AS "clCode"
			     , CL_CODE_NM				AS "clCodeNm"
			     , USE_AT					AS "useAt"
			  FROM COMTC_CMMN_CL_CODE
			 WHERE USE_AT = 'Y'




				 AND CL_CODE LIKE '%' || #{searchKeyword} || '%'


				 AND CL_CODE_NM LIKE '%' || #{searchKeyword} || '%'



        /* 구현 Sql */

                   ) ALL_LIST
                   )
             WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

    INSERT INTO  COMTC_CMMN_CL_CODE (  
    	   CL_CODE
         , CL_CODE_NM
         , CL_CODE_DC
         , USE_AT
		 , FRST_REGISTER_ID 
		 , FRST_REGIST_PNTTM 
		 , LAST_UPDUSR_ID
		 , LAST_UPDT_PNTTM 
    ) VALUES (  
    	   #{clCode}
         , #{clCodeNm}
         , #{clCodeDc}
         , #{useAt}
		 , #{frstRegisterId}
		 , SYSDATE
		 , #{frstRegisterId} 
		 , SYSDATE
    )

mapper.update:

UPDATE COMTC_CMMN_CL_CODE
   SET CL_CODE_NM        = #{clCodeNm}
     , CL_CODE_DC        = #{clCodeDc}
     , USE_AT            = #{useAt}
     , LAST_UPDUSR_ID    = #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	 = SYSDATE
 WHERE CL_CODE           = #{clCode}

mapper.delete:

UPDATE COMTC_CMMN_CL_CODE
   SET USE_AT  = 'N'
 WHERE CL_CODE = #{clCode}
