mapper.select:
        /* 구현 Sql */

			SELECT A.CODE_ID				AS "codeId"
			     , B.CODE_ID_NM				AS "codeIdNm"
			     , A.CODE					AS "code"
			     , A.CODE_NM				AS "codeNm"
			     , A.USE_AT					AS "useAt"
			  FROM COMTC_CMMN_DETAIL_CODE A
				   LEFT OUTER JOIN COMTC_CMMN_CODE B 
					    ON A.CODE_ID = B.CODE_ID
			 WHERE 1=1




				 AND A.CODE_ID = #{searchKeyword} 


				 AND A.CODE = #{searchKeyword} 


				 AND A.CODE_NM LIKE '%'||#{searchKeyword}||'%'




			 ORDER BY A.CODE_ID ASC, A.CODE ASC

        /* 구현 Sql */

 			LIMIT  #{recordPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM COMTC_CMMN_DETAIL_CODE A LEFT OUTER JOIN COMTC_CMMN_CODE B ON A.CODE_ID = B.CODE_ID WHERE 1=1
AND A.CODE_ID = #{searchKeyword}
AND A.CODE = #{searchKeyword}
AND A.CODE_NM LIKE '%'||#{searchKeyword}||'%'

SELECT A.CODE_ID AS "codeId" , B.CODE_ID_NM AS "codeIdNm" , A.CODE AS "code" , A.CODE_NM AS "codeNm" , A.USE_AT AS "useAt" FROM COMTC_CMMN_DETAIL_CODE A LEFT OUTER JOIN COMTC_CMMN_CODE B ON A.CODE_ID = B.CODE_ID WHERE 1=1
AND A.CODE_ID = #{searchKeyword}
AND A.CODE = #{searchKeyword}
AND A.CODE_NM LIKE '%'||#{searchKeyword}||'%'
ORDER BY A.CODE_ID ASC, A.CODE ASC

mapper.insert:

    INSERT INTO  COMTC_CMMN_DETAIL_CODE (  
    	   CODE_ID
         , CODE
         , CODE_NM
         , CODE_DC
         , USE_AT
		 , FRST_REGISTER_ID 
		 , FRST_REGIST_PNTTM 
		 , LAST_UPDUSR_ID
		 , LAST_UPDT_PNTTM 
    ) VALUES (  
    	   #{codeId}
         , #{code}
         , #{codeNm}
         , #{codeDc}
         , #{useAt}
		 , #{frstRegisterId}
		 , sysdate()
		 , #{frstRegisterId} 
		 , sysdate()
    )

mapper.update:

UPDATE COMTC_CMMN_DETAIL_CODE
   SET CODE_NM           = #{codeNm}
     , CODE_DC           = #{codeDc}
     , USE_AT            = #{useAt}
     , LAST_UPDUSR_ID    = #{lastUpdusrId}
     , LAST_UPDT_PNTTM 	 = sysdate()
 WHERE CODE_ID   = #{codeId}
   AND CODE      = #{code}

mapper.delete:

UPDATE COMTC_CMMN_DETAIL_CODE
   SET USE_AT    = 'N'
 WHERE CODE_ID   = #{codeId}
   AND CODE      = #{code}
