mapper.select:
/* 구현 Sql */
SELECT OCCRRNC_DE , INSTT_CODE , OPERT_SN , CHANGE_SE_CODE , PROCESS_SE , ETC_CODE , ALL_INSTT_NM , LOWEST_INSTT_NM , INSTT_ABRV_NM , ODR , ORD , INSTT_ODR , BEST_INSTT_CODE , UPPER_INSTT_CODE , REPRSNT_INSTT_CODE , INSTT_TY_LCLAS , INSTT_TY_MLSFC , INSTT_TY_SCLAS , TELNO , FXNUM , CREAT_DE , ABL_DE , ABL_ENNC , CHANGE_DE , CHANGE_TIME , BSIS_DE , SORT_ORDR FROM COMTH_INSTT_CODE_LOG WHERE 1 = 1
AND ALL_INSTT_NM LIKE '%'||#{searchVO.searchKeyword}||'%'
AND INSTT_CODE = #{insttCode}
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

SELECT COUNT(*) totcnt FROM COMTH_INSTT_CODE_LOG WHERE 1 = 1
AND ALL_INSTT_NM LIKE '%'||#{searchVO.searchKeyword}||'%'
AND INSTT_CODE = #{insttCode}

