mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT ZIP AS "zip" , SN AS "sn" , CTPRVN_NM AS "ctprvnNm" , SIGNGU_NM AS "signguNm" , EMD_NM AS "emdNm" , LI_BULD_NM AS "liBuldNm" , LNBR_DONG_HO AS "lnbrDongHo" FROM COMTC_ZIP WHERE 1 = 1
AND ZIP LIKE '%'|| #{searchKeyword} ||'%'
AND CTPRVN_NM LIKE '%'|| #{searchKeyword} ||'%'
AND SIGNGU_NM LIKE '%'|| #{searchKeyword} ||'%'
AND EMD_NM LIKE '%'|| #{searchKeyword} ||'%'
AND LI_BULD_NM LIKE '%'|| #{searchKeyword} ||'%'
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordPerPage}

mapper.insert:

SELECT MAX(SN)+1	sn
  FROM COMTC_ZIP



INSERT INTO  COMTC_ZIP (  
	   ZIP
     , SN
     , CTPRVN_NM
     , SIGNGU_NM
     , EMD_NM
     , LI_BULD_NM
     , LNBR_DONG_HO
	 , FRST_REGISTER_ID 
	 , FRST_REGIST_PNTTM 
	 , LAST_UPDUSR_ID
	 , LAST_UPDT_PNTTM 
) VALUES (  
	   #{zip}
     , #{sn}
     , #{ctprvnNm}
     , #{signguNm}
     , #{emdNm}
     , #{liBuldNm}
     , #{lnbrDongHo}
	 , #{frstRegisterId}
	 , SYSDATE
	 , #{frstRegisterId} 
	 , SYSDATE
)

mapper.update:

UPDATE COMTC_ZIP
   SET CTPRVN_NM         = #{ctprvnNm}
     , SIGNGU_NM         = #{signguNm}
     , EMD_NM            = #{emdNm}
     , LI_BULD_NM        = #{liBuldNm}
     , LNBR_DONG_HO      = #{lnbrDongHo}
     , LAST_UPDUSR_ID    = #{lastUpdusrId}
     , LAST_UPDT_PNTTM   = SYSDATE
 WHERE ZIP               = #{zip}
   AND SN                = #{sn}

