mapper.select:
/* 구현 Sql */
SELECT a.REQUST_ID AS "requstId" , a.OCCRRNC_DE AS "occrrncDe" , a.METHOD_NM AS "methodNm" , c.CODE_NM AS "processSeCodeNm" , a.PROCESS_TIME AS "processTime" , b.USER_NM AS "rqesterNm" FROM COMTH_SYS_LOG a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.RQESTER_ID = b.ESNTL_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM033') c ON a.PROCESS_SE_CODE = c.CODE WHERE 1=1
AND a.CODE_NM LIKE '%'||#{searchVO.searchKeyword}||'%'
AND a.OCCRRNC_DE BETWEEN #{searchBgnDe} AND #{searchEndDe}
ORDER BY a.OCCRRNC_DE DESC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

SELECT COUNT(a.REQUST_ID) as cnt FROM COMTH_SYS_LOG a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.RQESTER_ID = b.ESNTL_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM033') c ON a.PROCESS_SE_CODE = c.CODE WHERE 1=1
AND a.CODE_NM LIKE '%'||#{searchVO.searchKeyword}||'%'
AND a.OCCRRNC_DE BETWEEN #{searchBgnDe} AND #{searchEndDe}

mapper.insert:

INSERT INTO COMTH_SYS_LOG ( 
		REQUST_ID
	  , SVC_NM
	  , METHOD_NM
	  , PROCESS_SE_CODE
	  , PROCESS_TIME
	  , RQESTER_ID
	  , RQESTER_IP
	  , OCCRRNC_DE 
) VALUES ( 
		#{requstId}
	  , #{srvcNm}
	  , #{methodNm}
	  , #{processSeCode}
	  , #{processTime}
	  , #{rqesterId}#
	  , #{rqesterIp}
	  , now()
)
