mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT a.REQUST_ID AS "requstId" , a.OCCRRNC_DE AS "occrrncDe" , c.CODE_NM AS "trsmrcvSeCodeNm" , a.CNTC_ID AS "cntcId" , a.PROVD_INSTT_ID AS "provdInsttId" , a.REQUST_INSTT_ID AS "requstInsttId" , b.USER_NM AS "rqesterNm" FROM COMTH_TRSMRCV_LOG a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.RQESTER_ID = b.ESNTL_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM008') c ON a.TRSMRCV_SE_CODE = c.CODE WHERE 1=1
AND c.CODE_NM LIKE '%'||#{searchVO.searchKeyword}||'%'
AND a.OCCRRNC_DE BETWEEN #{searchBgnDe} AND #{searchEndDe}
ORDER BY a.OCCRRNC_DE DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{searchVO.firstIndex} + 1 AND #{searchVO.firstIndex} + #{searchVO.recordPerPage}

SELECT COUNT(a.REQUST_ID) as cnt FROM COMTH_TRSMRCV_LOG a LEFT OUTER JOIN COMVN_USER_MASTER b ON a.RQESTER_ID = b.ESNTL_ID LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM008') c ON a.TRSMRCV_SE_CODE = c.CODE WHERE 1=1
AND c.CODE_NM LIKE '%'||#{searchVO.searchKeyword}||'%'
AND a.OCCRRNC_DE BETWEEN #{searchBgnDe} AND #{searchEndDe}

mapper.insert:

INSERT INTO COMTH_TRSMRCV_LOG ( 
		REQUST_ID
