	  , REQUST_TRNSMIT_TM
	  , REQUST_RECPTN_TM
	  , RSPNS_TRNSMIT_TM
	  , RSPNS_RECPTN_TM
	  , RESULT_CODE
	  , RESULT_MSSAGE
	  , RQESTER_ID
	  , FRST_REGIST_PNTTM
	  , OCCRRNC_DE 
) VALUES ( 
		#{requstId, jdbcType=VARCHAR}
	  , #{trsmrcvSeCode, jdbcType=VARCHAR}
	  , #{cntcId, jdbcType=VARCHAR}
	  , #{provdInsttId, jdbcType=VARCHAR}
	  , #{provdSysId, jdbcType=VARCHAR}
	  , #{provdSvcId, jdbcType=VARCHAR}
	  , #{requstInsttId, jdbcType=VARCHAR}
	  , #{requstSysId, jdbcType=VARCHAR}
	  , #{requstTrnsmitTm, jdbcType=VARCHAR}
	  , #{requstRecptnTm, jdbcType=VARCHAR}
	  , #{rspnsTrnsmitTm, jdbcType=VARCHAR}
	  , #{rspnsRecptnTm, jdbcType=VARCHAR}
	  , #{resultCode, jdbcType=VARCHAR}
	  , #{resultMessage, jdbcType=VARCHAR}
	  , #{rqesterId, jdbcType=VARCHAR}
	  , SYSDATE
	  , TO_CHAR(sysdate, 'YYYYMMDD')
)
