mapper.select:
        /* 구현 Sql */

			SELECT MENU_NO           		AS "menuNo"
				 , MENU_NM           		AS "menuNm"
				 , UPPER_MENU_NO     		AS "upperMenuId"
				 , MENU_DC           		AS "menuDc"
				 , PROGRM_FILE_NM    		AS "progrmFileNm"
		 	  FROM COMTN_MENU_INFO 
			 WHERE MENU_NM like '%'||#{searchKeyword}||'%'

        /* 구현 Sql */

 			LIMIT  #{recordPerPage} OFFSET #{firstIndex}

mapper.insert:

			INSERT INTO COMTN_MENU_INFO ( 
					MENU_NO
            	  , MENU_ORDR
                  , MENU_NM
                  , UPPER_MENU_NO
            	  , MENU_DC
            	  , RELATE_IMAGE_PATH
            	  , RELATE_IMAGE_NM
            	  , PROGRM_FILE_NM
            ) VALUES (   
            		#{menuNo}
                  , #{menuOrdr}
                  , #{menuNm}  
                  , #{upperMenuId}
                  , #{menuDc}
                  , #{relateImagePath}
                  , #{relateImageNm}
                  , #{progrmFileNm} 
            )

mapper.update:

			UPDATE COMTN_MENU_INFO 
			   SET MENU_ORDR         = #{menuOrdr}       
            	 , MENU_NM           = #{menuNm}         
            	 , UPPER_MENU_NO     = #{upperMenuId}    
            	 , MENU_DC           = #{menuDc}         
            	 , RELATE_IMAGE_PATH = #{relateImagePath}
            	 , RELATE_IMAGE_NM   = #{relateImageNm}  
            	 , PROGRM_FILE_NM    = #{progrmFileNm}   
			 WHERE MENU_NO = #{menuNo}

mapper.select:

SELECT COUNT(MENU_NO) 			AS "totcnt"
  FROM COMTN_MENU_INFO
 WHERE MENU_NO = #{menuNo}


SELECT COUNT(MENU_NO) 			AS totcnt
  FROM COMTN_MENU_INFO
 WHERE UPPER_MENU_NO = #{menuNo}
