mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT REQUST_NO AS "rqestNo" , PROGRM_FILE_NM AS "progrmFileNm" , REQUSTER_ID AS "rqestPersonId" , trim(PROCESS_STTUS_CODE) AS "processSttus" , REQUST_DE AS "rqestDe" , REQUST_SJ AS "rqestSj" FROM COMTN_PROGRM_CHANGE_DTLS WHERE PROGRM_FILE_NM like '%'||#{searchVO.searchKeyword}||'%'
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM BETWEEN #{searchVO.firstIndex} + 1 AND #{searchVO.firstIndex} + #{searchVO.recordPerPage}

mapper.insert:

			INSERT INTO COMTN_PROGRM_CHANGE_DTLS (  
				   PROGRM_FILE_NM
             	 , REQUST_NO
              	 , REQUSTER_ID
              	 , CHANGE_REQUST_CN
              	 , REQUST_DE
             	 , REQUST_SJ 
              	 , PROCESS_STTUS_CODE
            ) VALUES (  
            	   #{progrmFileNm}
                 , #{rqestNo}
                 , #{rqestPersonId}
                 , #{changeRqestCn}
                 , #{rqestDe}
                 , #{rqestSj} 
                 , 'A'
            )

mapper.update:

			UPDATE COMTN_PROGRM_CHANGE_DTLS
			   SET REQUSTER_ID        	= #{rqestPersonId} 
			     , CHANGE_REQUST_CN  	= #{changeRqestCn}
                 , REQUST_DE        	= #{rqestDe}       
                 , REQUST_SJ        	= #{rqestSj} 
			 WHERE PROGRM_FILE_NM		= #{progrmFileNm}
			   AND REQUST_NO    		= #{rqestNo}

mapper.delete:

    DELETE 
      FROM COMTN_PROGRM_CHANGE_DTLS
	 WHERE PROGRM_FILE_NM		= #{progrmFileNm}
	   AND REQUST_NO    		= #{rqestNo}

mapper.update:

			UPDATE COMTN_PROGRM_CHANGE_DTLS
