mapper.select:

            SELECT *
              FROM (
            SELECT ROWNUM RNUM, ALL_LIST.*
              FROM (

        /* 구현 Sql */

		    SELECT REQUST_NO 				AS "rqestNo"
				 , PROGRM_FILE_NM 		 	AS "progrmFileNm"
				 , REQUSTER_ID 				AS "rqestPersonId"
				 , trim(PROCESS_STTUS_CODE) AS "processSttus" 
				 , REQUST_DE 				AS "rqestDe"
				 , REQUST_SJ 				AS "rqestSj"
			  FROM COMTN_PROGRM_CHANGE_DTLS
			 WHERE 1=1




				 AND PROCESS_STTUS_CODE like '%'||#{searchVO.searchKeyword}||'%' 


				 AND PROCESS_STTUS_CODE = #{searchVO.searchKeyword}


				 AND REQUSTER_ID like  '%'||#{searchVO.searchKeyword}||'%'




				 AND REQUST_DE between #{searchKeywordFrom} and #{searchKeywordTo} 


             ORDER BY REQUST_NO ASC

        /* 구현 Sql */

                   ) ALL_LIST
                   )
             WHERE RNUM BETWEEN #{searchVO.firstIndex} + 1 AND #{searchVO.firstIndex} + #{searchVO.recordPerPage}

SELECT COUNT(REQUST_NO) AS "totcnt" FROM COMTN_PROGRM_CHANGE_DTLS WHERE 1=1
AND PROCESS_STTUS_CODE like '%'||#{searchVO.searchKeyword}||'%'
AND PROCESS_STTUS_CODE = #{searchVO.searchKeyword}
AND REQUSTER_ID like '%'||#{searchVO.searchKeyword}||'%'
AND REQUST_DE between #{searchKeywordFrom} and #{searchKeywordTo}


SELECT user_email AS tmpEmail 
  FROM COMVN_USER_MASTER
 WHERE user_id = #{rqestPersonId}

