mapper.select:
/* 구현 Sql */
SELECT A.BACKUP_OPERT_ID , A.EXECUT_CYCLE , C.CODE_NM AS "executCycleNm" , A.EXECUT_SCHDUL_DE , A.EXECUT_SCHDUL_HOUR , A.EXECUT_SCHDUL_MNT , A.EXECUT_SCHDUL_SECND , A.BACKUP_OPERT_NM , A.BACKUP_ORGINL_DRCTRY , A.BACKUP_STRE_DRCTRY , A.CMPRS_SE , B.CODE_NM AS "cmprsSeNm" , A.LAST_UPDT_PNTTM AS "lastUpdusrPnttm" , A.LAST_UPDUSR_ID , A.FRST_REGISTER_ID , A.FRST_REGIST_PNTTM AS "frstRegisterPnttm" FROM COMTN_BACKUP_OPERT A LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM049') B ON A.CMPRS_SE = B.CODE LEFT OUTER JOIN (SELECT CODE, CODE_NM FROM COMTC_CMMN_DETAIL_CODE WHERE CODE_ID = 'COM047') C ON A.EXECUT_CYCLE = C.CODE WHERE A.USE_AT = 'Y'
AND A.BACKUP_OPERT_NM LIKE '%'|| #{searchVO.searchKeyword} ||'%'
AND A.BACKUP_ORGINL_DRCTRY LIKE '%'|| #{searchVO.searchKeyword} ||'%'
ORDER BY A.BACKUP_OPERT_ID ASC
/* 구현 Sql */
LIMIT #{searchVO.recordPerPage} OFFSET #{searchVO.firstIndex}

mapper.insert:

            INSERT INTO COMTN_BACKUP_OPERT (
                	BACKUP_OPERT_ID
                  , BACKUP_OPERT_NM
                  , BACKUP_ORGINL_DRCTRY
                  , BACKUP_STRE_DRCTRY
                  , CMPRS_SE
                  , EXECUT_CYCLE
                  , EXECUT_SCHDUL_DE
                  , EXECUT_SCHDUL_HOUR
                  , EXECUT_SCHDUL_MNT
                  , EXECUT_SCHDUL_SECND
                  , USE_AT
				  , FRST_REGISTER_ID 
				  , FRST_REGIST_PNTTM 
				  , LAST_UPDUSR_ID
				  , LAST_UPDT_PNTTM 
            ) VALUES (
                    #{backupOpertId}
                  , #{backupOpertNm}
                  , #{backupOrginlDrctry}
                  , #{backupStreDrctry}
                  , #{cmprsSe}
                  , #{executCycle}
                  , #{executSchdulDe}
                  , #{executSchdulHour}
                  , #{executSchdulMnt}
                  , #{executSchdulSecnd}
                  , 'Y'
				  , #{frstRegisterId}
				  , now()
				  , #{frstRegisterId} 
				  , now()
            )

mapper.update:

UPDATE COMTN_BACKUP_OPERT 
   SET BACKUP_OPERT_NM 			= #{backupOpertNm}
     , BACKUP_ORGINL_DRCTRY 	= #{backupOrginlDrctry}
     , BACKUP_STRE_DRCTRY 		= #{backupStreDrctry}
     , CMPRS_SE 				= #{cmprsSe}
     , EXECUT_CYCLE 			= #{executCycle}
     , EXECUT_SCHDUL_DE 		= #{executSchdulDe}
     , EXECUT_SCHDUL_HOUR 		= #{executSchdulHour}
     , EXECUT_SCHDUL_MNT 		= #{executSchdulMnt}
     , EXECUT_SCHDUL_SECND 		= #{executSchdulSecnd}
     , LAST_UPDUSR_ID 			= #{lastUpdusrId}
     , LAST_UPDT_PNTTM 			= now()
 WHERE BACKUP_OPERT_ID = #{backupOpertId}

mapper.delete:

UPDATE COMTN_BACKUP_OPERT 
   SET USE_AT = 'N'
 WHERE BACKUP_OPERT_ID = #{backupOpertId}

mapper.insert:

INSERT INTO COMTN_BACKUP_SCHDUL_DFK (
    	BACKUP_OPERT_ID
      , EXECUT_SCHDUL_DFK_SE
) VALUES (
        #{backupOpertId}
      , #{executSchdulDfkSe}
)
